INSERT INTO `types` VALUES (1,'Page','page','A page is a simple method for creating and displaying information that rarely changes, such as an \"About us\" section of a website. By default, a page entry does not allow visitor comments.',0,0,0,1,0,0,'','','2009-09-09 00:23:24','2009-09-02 18:06:27'),(2,'Blog','blog','A blog entry is a single post to an online journal, or blog.',1,1,2,1,0,0,'','','2009-09-15 12:15:43','2009-09-02 18:20:44'),(4,'Node','node','Default content type.',1,1,2,1,0,0,'','','2009-10-06 21:53:15','2009-09-05 23:51:56'),(5,'Tourism','visit','Pages for tourism promotions',0,0,0,0,0,0,'',NULL,'2012-09-12 04:39:36','2012-09-12 04:39:36'),(6,'Payong Panahon','news','News',0,0,2,0,0,0,'',NULL,'2012-09-24 06:59:22','2012-09-24 06:51:03'),(7,'Mata ng Bagyo','announcements',' Announcements',0,0,2,0,0,0,'',NULL,'2012-09-24 06:59:13','2012-09-24 06:51:28');
